import React, { Component, useEffect, useState } from "react";
import { gql } from "apollo-boost";
import { graphql } from "react-apollo";
import compose from "recompose/compose";
import { getDealersQuery, addProductQuery } from "../queries/queries";

class AddProduct extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "",
      color: "",
      category: "",
      createdBy: "",
    };
  }
  createNewProductBtn = (e) => {
    console.log(this.state);
  };
  render() {
    return (
      <div>
        <h2>Добавить продукт</h2>
        <input
          type="text"
          placeholder="Введите имя"
          onChange={(e) => this.setState({ name: e.target.value })}
        />
        <input
          type="text"
          placeholder="Категория"
          onChange={(e) => this.setState({ category: e.target.value })}
        />
        <input
          type="text"
          placeholder="Цвет"
          onChange={(e) => this.setState({ color: e.target.value })}
        />
        <select onChange={(e) => this.setState({ createdBy: e.target.value })}>
          {!this.props.getDealersQuery.loading &&
            this.props.getDealersQuery.dealers.map((element) => {
              return <option value={element.id}>{element.name}</option>;
            })}
        </select>
        <button onClick={createNewProductBtn}>Создать</button>
      </div>
    );
  }
}

export default compose(
  graphql(getDealersQuery, { name: "getDealersQuery" }),
  graphql(addProductQuery, { name: "addProductQuery" })
)(AddProduct);
