import React, { Component } from "react";
import { graphql } from "react-apollo";
import compose from "recompose/compose";
import { getProductsQuery, updateProductsQuery } from "../queries/queries";
import Button from "@material-ui/core/Button";

class updateProduct extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "",
      color: "",
      category: "",
      createdBy: "",
    };
  }
  render() {
    console.log(this.props);
    return (
      <div>
        <div>
          <input
            type="text"
            placeholder="Новое имя"
            onChange={() => this.setState({ name: e.taeget.value })}
          />
        </div>
        <div>
          <input
            type="text"
            placeholder="Новая Категория"
            onChange={() => this.setState({ color: e.taeget.value })}
          />
        </div>
        <div>
          <input
            type="text"
            placeholder="Новый цвет"
            onChange={() => this.setState({ createdBy: e.target.value })}
          />
        </div>
        <div>
          <input
            type="text"
            placeholder="Новый производитель"
            onChange={() => this.setState({ category: e.taeget.value })}
          />
        </div>
        <button onClick={() => console.log(this.state)}>Сохранить</button>
      </div>
    );
  }
}

export default compose(
  graphql(getProductsQuery, { name: "getDealersQuery" }),
  graphql(updateProductsQuery, { name: "addProductQuery" })
)(updateProduct);
