import React, { Component } from "react";
import { graphql } from "react-apollo";
import compose from "recompose/compose";
import { updateProductsQuery } from "../queries/queries";
import Button from "@material-ui/core/Button";

class updateProduct extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "",
      color: "",
      category: "",
      createdBy: "",
      selected: false,
    };
  }
  render() {

    const updateProduct = () => {
      this.state.selected = true;
    };
    return (
      <div>
        <Button variant="contained" color="primary" onClick={updateProduct}>
          Редактировать
        </Button>
        {this.state.selected ? (
          <div
            style={{
              width: "100%",
              position: "absolute",
              top: "0",
              left: "0",
              right: "0",
              bottom: "0",
              background: "black",
            }}
          >
            <input type="text" />
          </div>
        ) : (
          ""
        )}
      </div>
    );
  }
}

export default compose(
  graphql(updateProductsQuery, { name: "updateProductsQuery" })
)(updateProduct);
