import React, { Component } from "react";
import { graphql } from "react-apollo";
import compose from "recompose/compose";
import { getProductsQuery, updateProductsQuery } from "../queries/queries";
import Button from "@material-ui/core/Button";

class updateProduct extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "",
      color: "",
      category: "",
      createdBy: "",
    };
  }
  render() {
    console.log(this.props);
    return (
      <div>
        <div>
          <input type="text" placeholder="Новое имя" onChange={this.setState({})}/>
        </div>
        <div>
          <input type="text" placeholder="Новая Категория" onChange={this.setState({})}/>
        </div>
        <div>
          <input type="text" placeholder="Новый цвет" onChange={this.setState({})}/>
        </div>
        <button>Сохранить</button>
      </div>
    );
  }
}

export default compose(
  graphql(getProductsQuery, { name: "getDealersQuery" }),
  graphql(updateProductsQuery, { name: "addProductQuery" })
)(updateProduct);
