import React, { Component } from "react";
import { gql } from "apollo-boost";
import { graphql } from "react-apollo";
import { getProductsQuery } from "../queries/queries";
import DeleteProduct from "./deleteProduct";
import ProductDetails from "./productDetails";

class ProductList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      selected: null,
    };
  }
  render() {
    return (
      <div>
        {this.props.data.loading ? (
          <div>Загрузка...</div>
        ) : (
          <ul className="products-table">
            {this.props.data.products.map((element) => {
              return (
                <li
                  className="products-table__element"
                  onClick={() => {
                    this.setState({ selected: element.id });
                  }}
                >
                  <div>{element.model_name}</div>
                  <div>Редактировать</div>
                  <DeleteProduct id={element.id} />
                </li>
              );
            })}
          </ul>
        )}
        <ProductDetails productId={this.state.selected} />
      </div>
    );
  }
}

export default graphql(getProductsQuery)(ProductList);
