import React, { Component } from "react";
import compose from "recompose/compose";
import { graphql } from "react-apollo";
import { getProductQuery } from "../queries/queries";
import UpdateProduct from "./updateProduct";

class ProductDetails extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "",
      color: "",
      category: "",
      createdBy: "",
      form: false,
    };
  }
  render() {
    return (
      <div style={{ width: "90%", margin: "20px auto", padding: "2vh" }}>
        <h2>Детали продукта</h2>
        <div>
          {this.props.data.product && (
            <div>
              <h4>Название: {this.props.data.product.model_name} </h4>
              <div>Категория: {this.props.data.product.model_category} </div>
              <div>Создатель: {this.props.data.product.createdBy.name}</div>
              <div>
                Возраст производителя: {this.props.data.product.createdBy.age}
              </div>
            </div>
          )}
        </div>
        <button
          onClick={(e) => {
            this.setState({ form: true });
          }}
        >
          Изменить
        </button>
        {this.state.form && (
          <>
            <div>
              <TextField
                id="standard-basic"
                label="Введите имя"
                type="text"
                onChange={(e) => this.setState({ name: e.target.value })}
              />
            </div>
            <div>
              <TextField
                id="standard-basic"
                label="Категория"
                onChange={(e) => this.setState({ category: e.target.value })}
              />
            </div>
            <div>
              <TextField
                id="standard-basic"
                label="Цвет"
                onChange={(e) => this.setState({ color: e.target.value })}
              />
            </div>
            <select
              onChange={(e) =>
                this.setState({ createdBy: Number(e.target.value) })
              }
            >
              {!this.props.getDealersQuery.loading &&
                this.props.getDealersQuery.dealers.map((element) => {
                  return <option value={element.id}>{element.name}</option>;
                })}
            </select>
          </>
        )}
      </div>
    );
  }
}

export default graphql(getProductQuery, {
  options: (props) => {
    return {
      variables: {
        id: props.productId,
      },
    };
  },
})(ProductDetails);
