import React, { Component } from "react";
import { gql } from "apollo-boost";
import { graphql } from "react-apollo";
import { getProductsQuery } from "../queries/queries";
import { Button } from "@material-ui/core";

class ProductList extends Component {
  render() {
    return (
      <div>
        {this.props.data.loading ? (
          <div>Загрузка</div>
        ) : (
          <ul className="products-table">
            {this.props.data.products.map((element) => {
              return (
                <li className="products-table__element">
                  <div>{element.model_name}</div>
                  <div>{element.model_category}</div>
                  {/* <div>Удалить</div> */}
                </li>
              );
            })}
            <li>Product</li>
          </ul>
        )}
        <h2>Добавить пост</h2>
      </div>
    );
  }
}

export default graphql(getProductsQuery)(ProductList);
