import React, { Component } from "react";
import { graphql } from "react-apollo";
import compose from "recompose/compose";
import { updateProductsQuery } from "../queries/queries";
import Button from "@material-ui/core/Button";

class updateProduct extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "",
      color: "",
      category: "",
      createdBy: "",
      form: false,
    };
  }
  render() {
    const updateProduct = () => {
      return (this.state.selected = true);
    };
    return (
      <div>
        <div onClick={updateProduct}>
          <Button variant="contained" color="primary">
            Редактировать
          </Button>
        </div>
        {this.state.form && <div>Хуй</div>}
      </div>
    );
  }
}

export default compose(
  graphql(updateProductsQuery, { name: "updateProductsQuery" })
)(updateProduct);
