import React, { Component } from "react";
import { graphql } from "react-apollo";
import compose from "recompose/compose";
import { getProductsQuery, updateProductsQuery } from "../queries/queries";
import Button from "@material-ui/core/Button";

class updateProduct extends Component {
  constructor(props) {
    super(props);
    this.state = {
      id: "",
      name: "",
      color: "",
      category: "",
      createdBy: "",
    };
  }
  render() {
    console.log(this.props.id);
    const updateProduct = () => {
      this.setState({ id: this.props.userData.createdBy.id });
      console.log(this.state);
      //   this.props.updateProductsQuery({
      //     variables: {
      //       id: this.props.userData.createdBy.id,
      //       name: this.state.name,
      //       category: this.state.category,
      //       color: this.state.color,
      //       createdBy: +this.state.createdBy,
      //     },
      //     refetchQueries: [{ query: updateProductsQuery }],
      //   });
    };

    return (
      <div>
        <div>
          <input
            type="text"
            placeholder="Новое имя"
            onChange={(e) => this.setState({ name: e.target.value })}
          />
        </div>
        <div>
          <input
            type="text"
            placeholder="Новая Категория"
            onChange={(e) => this.setState({ color: e.target.value })}
          />
        </div>
        <div>
          <input
            type="text"
            placeholder="Новый цвет"
            onChange={(e) => this.setState({ createdBy: e.target.value })}
          />
        </div>
        <div>
          <input
            type="text"
            placeholder="Новый производитель"
            onChange={(e) =>
              this.setState({ category: Number(e.target.value) })
            }
          />
        </div>
        <button onClick={updateProduct}>Сохранить</button>
      </div>
    );
  }
}

export default compose(
  graphql(getProductsQuery, { name: "getDealersQuery" }),
  graphql(updateProductsQuery, { name: "updateProductsQuery" })
)(updateProduct);
