import actionTypes from "../constants";

const initialState = {
  products: [],
  fetchedProducts: [],
};

export const productsReducer = (state = initialState, action) => {
  const { type, payload } = action;
  switch (type) {
    case actionTypes.PRODUCTS_FETCH_SUCCESS: {
    }
  }
};
