import React, { Component } from "react";
import compose from "recompose/compose";
import { graphql } from "react-apollo";
import {
  getDealersQuery,
  addProductQuery,
  getProductsQuery,
} from "../queries/queries";
import Button from "@material-ui/core/Button";
import { connect } from "react-redux";
import TextField from "@material-ui/core/TextField";

class AddProduct extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "",
      color: "",
      category: "",
      createdBy: "",
    };
  }

  render() {
    const createNewProductBtn = (e) => {
      this.props.addProductQuery({
        variables: {
          name: this.state.name,
          category: this.state.category,
          color: this.state.color,
          createdBy: +this.state.createdBy,
        },
        refetchQueries: [{ query: getProductsQuery }],
      });
    };

    return (
      <div>
        <h3>Добавить продукт</h3>
        <div>
          <TextField
            id="standard-basic"
            label="Введите имя"
            type="text"
            onChange={(e) => this.setState({ name: e.target.value })}
          />
        </div>
        <div>
          <TextField
            id="standard-basic"
            label="Категория"
            onChange={(e) => this.setState({ category: e.target.value })}
          />
        </div>
        <div>
          <TextField
            id="standard-basic"
            label="Цвет"
            onChange={(e) => this.setState({ color: e.target.value })}
          />
        </div>
        <select
          onChange={(e) => this.setState({ createdBy: Number(e.target.value) })}
        >
          {!this.props.getDealersQuery.loading &&
            this.props.getDealersQuery.dealers.map((element) => {
              return <option value={element.id}>{element.name}</option>;
            })}
        </select>

        <div>
          <Button
            variant="contained"
            color="primary"
            onClick={createNewProductBtn}
          >
            Создать
          </Button>
        </div>
      </div>
    );
  }
}

export default compose(
  graphql(getDealersQuery, { name: "getDealersQuery" }),
  graphql(addProductQuery, { name: "addProductQuery" })
)(AddProduct);
