import { gql } from "apollo-boost";

const getProductsQuery = gql`
  query {
    products {
      id
      model_name
      model_color
      model_category
      createdBy {
        id
      }
    }
  }
`;

const getDealersQuery = gql`
  query {
    dealers {
      name
      age
    }
  }
`;

const addProductQuery = gql`
  mutation($name: String, ) {
    addProduct(name: "", category: "", color: "", createdBy: "") {
      model_name
      model_category
      id
    }
  }
`;

export { getProductsQuery, getDealersQuery, addProductQuery };
