import React, { Component } from "react";
import { gql } from "apollo-boost";
import compose from "recompose/compose";
import { graphql } from "react-apollo";
import { getProductsQuery, updateProductsQuery } from "../queries/queries";
import DeleteProduct from "./deleteProduct";
import ProductDetails from "./productDetails";
import UpdateProduct from "./updateProduct";

class ProductList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      selected: null,
      form: false,
    };
  }

  render() {
    const updateProductHandler = (e) => {
      this.state.form = true;
      console.log(this.state);
    };

    return (
      <div>
        {this.props.getProductsQuery && this.props.getProductsQuery.loading ? (
          <div>Загрузка...</div>
        ) : (
          <ul
            style={{
              display: "flex",
              justifyContent: "space-between",
              flexWrap: "wrap",
              height: "auto",
            }}
          >
            {this.props.getProductsQuery.products.map((element) => {
              return (
                <li
                  style={{
                    display: "flex",
                    justifyContent: "space-between",
                    width: "80%",
                    margin: "auto",
                  }}
                >
                  <div
                    style={{ width: "25%" }}
                    onClick={() => {
                      this.setState({ selected: element.id });
                    }}
                  >
                    {element.model_name}
                  </div>
                  <div style={{ width: "25%" }}>{element.model_category}</div>

                  <div
                    style={{ width: "25%" }}
                    onClick={() => updateProductHandler(element.id)}
                  >
                    Удалить
                  </div>

                  <DeleteProduct style={{ width: "25%" }} id={element.id} />
                </li>
              );
            })}
          </ul>
        )}

        {this.state.form && <div>Хуй</div>}

        {this.state.selected && (
          <ProductDetails productId={this.state.selected} />
        )}
      </div>
    );
  }
}

export default compose(
  graphql(getProductsQuery, { name: "getProductsQuery" }),
  graphql(updateProductsQuery, { name: "updateProductsQuery" })
)(ProductList);
