import React, { Component } from "react";
import { gql } from "apollo-boost";
import { connect } from "react-redux";
import { productsFetchStart } from "../redux/actions/products";
import { graphql } from "react-apollo";
import { getProductsQuery, updateProductsQuery } from "../queries/queries";
import DeleteProduct from "./deleteProduct";
import ProductDetails from "./productDetails";

class ProductList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      selected: null,
    };
  }

  componentDidMount() {
    this.props.dispatchProductsFetchStart();
  }

  render() {
    return (
      <div>
        {!this.props.products ? (
          <div>Загрузка...</div>
        ) : (
          <ul
            style={{
              display: "flex",
              justifyContent: "space-between",
              flexWrap: "wrap",
              height: "auto",
            }}
          >
            {this.props.getProductsQuery.products.map((element) => {
              return (
                <li
                  style={{
                    display: "flex",
                    justifyContent: "space-between",
                    width: "80%",
                    margin: "auto",
                  }}
                >
                  <div
                    style={{ width: "32%" }}
                    onClick={() => {
                      this.setState({ selected: element.id });
                    }}
                  >
                    {element.model_name}
                  </div>
                  <div style={{ width: "32%" }}>{element.model_category}</div>

                  <DeleteProduct style={{ width: "32%" }} id={element.id} />
                </li>
              );
            })}
          </ul>
        )}

        {this.state.selected && (
          <ProductDetails productId={this.state.selected} />
        )}
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    products: state.products.products,
  };
};

const mapDispatchToProps = {
  dispatchProductsFetchStart: productsFetchStart,
};

export default connect(mapStateToProps, mapDispatchToProps)(ProductList);
