import { gql } from "apollo-boost";

const getProductsQuery = gql`
  query {
    products {
      id
      model_name
      model_color
      model_category
      createdBy {
        id
      }
    }
  }
`;

const getDealersQuery = gql`
  query {
    dealers {
      id
      name
      age
    }
  }
`;

const addProductQuery = gql`
  mutation(
    $name: String!
    $category: String!
    $color: String!
    $createdBy: ID!
  ) {
    addProduct(
      name: $name
      category: $category
      color: $color
      createdBy: $createdBy
    ) {
      model_name
      model_category
      id
    }
  }
`;

const deleteProduct = gql`
  mutation($id: ID!) {
    deleteProduct {
      (id: $id)
  }
}
`;

export { getProductsQuery, getDealersQuery, addProductQuery, deleteProduct };
