import React, { Component } from "react";
import { graphql } from "react-apollo";
import compose from "recompose/compose";
import { deleteProduct, getProductsQuery } from "../queries/queries";
import Button from "@material-ui/core/Button";

class DeleteProduct extends Component {
  render() {
    const deleteProduct = () => {
      this.props.deleteProduct({
        variables: {
          id: this.props.id,
        },
        refetchQueries: [{ query: getProductsQuery }],
      });
    };
    return (
      <div>
        {/* <Button variant="contained" color="secondary" onClick={deleteProduct}>
          Удалить
        </Button> */}
        <div onClick={deleteHandler}>Удалить</div>
      </div>
    );
  }
}

export default compose(
  graphql(deleteProduct, { name: "deleteProduct" }),
  graphql(getProductsQuery, { name: "getProductsQuery" })
)(DeleteProduct);
