import ProductsService from "../../services/products";
import {
  call,
  put,
  takeEvery,
  select,
  all,
  take,
  takeLatest,
} from "redux-saga/effects";
import actionTypes from "../constants";
import { productsFetchSuccess, productsFetchError } from "../actions/products";

function* getProducts(action) {
  const id = action.payload.id;
  try {
    const data = yield call(ProductsService.getProductsQuery, id);
    yield put(productsFetchSuccess({ products: data.data.products }));
  } catch (error) {
    yield put(productsFetchError(error));
    console.log(error);
  }
}

export function* saga() {
  yield all([takeLatest(actionTypes.PRODUCTS_FETCH_START, getProducts)]);
}
