import React, { Component } from "react";
import { graphql } from "react-apollo";
import compose from "recompose/compose";
import { updateProductsQuery } from "../queries/queries";
import Button from "@material-ui/core/Button";

class updateProduct extends Component {
  render() {
    const updateProduct = () => {
      console.log("hello");
    };
    return (
      <div>
        <Button variant="contained" color="primary" onClick={updateProduct}>
          Редактировать
        </Button>
      </div>
    );
  }
}

export default compose(
  //   graphql(updateProductsQuery, { name: "updateProductsQuery" }),
  graphql(getDealersQuery, { name: "getDealersQuery" })
)(updateProduct);
